	//Function to update the map
	void updateMap(int x, int y, int level, char update)
	{
		switch (level)
		{
		case 1:
			map1[x][y] = update;
			break;
		case 2:
			map2[x][y] = update;
			break;
		case 3:
			map3[x][y] = update;
			break;
		default:
			break;
		}
	}

	//Reseting the map
	void reset()
	{
		map0 = backUpMap0;
		map1 = backUpMap1;
		map2 = backUpMap2;
		map3 = backUpMap3;
	}

	//Function to return all coordinates
	vector<pair<int, int>> findE(const vector<vector<char>>& m)
	{
		vector<pair<int, int>> indices;
		for (int i = 0; i < m.size(); ++i)
		{
			for (int j = 0; j < m[i].size(); ++j)
			{
				if (m[i][j] == 'E')
				{
					indices.emplace_back(i, j);
				}
			}
		}
		return indices;
	}

	vector<pair<int, int>> findL(const vector<vector<char>>& m)
	{
		vector<pair<int, int>> indices;
		for (int i = 0; i < m.size(); ++i)
		{
			for (int j = 0; j < m[i].size(); ++j)
			{
				if (m[i][j] == 'L')
				{
					indices.emplace_back(i, j);
				}
			}
		}
		return indices;
	}

